import React, {useState, useEffect} from 'react';
import './RiddleMessageComponent.css'
import x from '../../../x.svg'
export const Riddle = (props) => {


    return props.show ==="riddle"?(
        <div className='page'>
            <img src={x} alt='home' className='xSVG' onClick={() => props.changeShow("no show")}></img>
            <h1>Welcome to the semester's Riddle</h1>
            <p>1.) Solve the riddle and a secret Phrase will appear. Be the first to tell one of the board members the phrase and win a small prize</p> 
            <p>2.) Find a way to hack the website. Meaning, get the phrase by any way other than solving the riddle. Must show board members how. Win $100!!! (this is designed to be impossible to hack)</p>
            <button>Continue</button>
        </div>
    ):null
 }
