import React, {useState, useEffect} from 'react';
import './NameHeaderComponent.css'


export const NameHeader = (props) => {
    let name= ["S","K","I","D","M","O","R","E"," ","C","S"," ","C","L","U","B"]
    let nameToAdd =[]
    let intervalIDs = []
    const [currentLetter,SetCurrentLetter] = useState(0);
    name.forEach((letter)=> {
        nameToAdd.push(<h2 className='letter'>{letter}</h2>)
        intervalIDs.push(0)
    })

    const randomRGB = () => {
        return (Math.floor(Math.random()*256));
    }

    function startColorChanging() {
        setInterval(keepChangingColor,400);
        SetCurrentLetter((currentLetter%name.length)+1);
        setTimeout(stopInterval,300)
    }

    function keepChangingColor() {
        let intervalID = setInterval(function () {
        let red = randomRGB().toString();
        let green = randomRGB().toString();
        let blue = randomRGB().toString();
        document.getElementsByClassName("letter")[currentLetter].style.color = 'rgb('+red+', '+green+', '+blue+')';
        intervalIDs[currentLetter]= intervalID;
        },400);

    }

    function stopInterval(){
        clearInterval(intervalIDs[currentLetter-1]);
        document.getElementsByClassName("letter")[currentLetter].style.color = 'white';
    }


    React.useEffect(() => {
            setInterval(startColorChanging,10000)
    },[currentLetter])


    return (
        <div className='flexBox'>
            {nameToAdd}
        </div>
    )

}
