import React, {useState, useEffect} from 'react';
import './NameHeaderComponent.css'


export const NameHeader = (props) => {
    let name= ["S","K","I","D","M","O","R","E"," ","C","S"," ","C","L","U","B"]
    let nameToAdd =[]
    let intervals =[]
    const [currentLetter,SetCurrentLetter] = useState(0);
    name.forEach((letter)=> {
        nameToAdd.push(<h2 className='letter'>{letter}</h2>)
    })

    const randomRGB = () => {
        return (Math.floor(Math.random()*256));
    }


    function keepChangingColor(index) {
        let intervalID = setInterval(function () {
        let red = randomRGB().toString();
        let green = randomRGB().toString();
        let blue = randomRGB().toString();
        document.getElementsByClassName("letter")[index].style.color = 'rgb('+red+', '+green+', '+blue+')';
        },400);
        intervals.push(intervalID);
    }

    function stopInterval(intervalID){
        clearInterval(intervalID);
        document.getElementsByClassName("letter")[currentLetter].style.color = 'white';
    }


    React.useEffect(() => {
        let currentLetter=0;
        let currentLetter2 =0;
        let intervalID = setInterval(()=>{
            keepChangingColor(currentLetter);
            currentLetter++;
        },300)
        if (currentLetter==name.length+5) {
            clearInterval(intervalID)
            setInterval(()=> {
                stopInterval(intervals[currentLetter2])
                currentLetter2++;
            })
        }

    },[currentLetter])


    return (
        <div className='flexBox'>
            {nameToAdd}
        </div>
    )

}
