            </div>
        )
    }
    else if (props.show === "riddleMessage"){
        return (
            <div className='page'>
                <img src={x} alt='home' className='xSVG' onClick={() => props.changeShow("no show")}></img>
                <h1>The Riddle</h1>
                <h3>What do you call a man who fills a cup with water?</h3>
                <form>
                    <div>
                        <label name='answer'>Your answer</label>
                        <input type={Text} name='answer' id='answer' value={answer} onChange={handleChange}></input>
                    </div>
                    <div>
                        <button>Submit</button>
                    </div>
                </form>
            </div>
        )
    }
 }
