import React, {useState, useEffect} from 'react';

export const SpotlightComponent = (props) => {
    const [show, setShow] = useState(false);
    const onClick = () => {
        setShow(true)
    }

    return (
        <div className='spotlight'>
            <h4 className='titlespot'></h4>
            <img alt='home' className='houseSVG' onClick={onClick}></img>
            {show?<p>{props.discription}</p>:null}
        </div>
    )
}
