import React, {useState, useEffect} from 'react';
import './RiddleMessageComponent.css'
import x from '../../../x.svg'
export const Riddle = (props) => {
    const [secretNumber, setSecretNumber] = useState(0);
    const handleChange = (event) => {
        setAnswer(event.target.value);
    } 

    const handleSubmit = (event) => {
        event.preventDefault();
    }

    const handleClick = () => {
        setTimeout(()=>{
            let number= 0
            for (let i= 0; i<answer.length;i++){
                number= number + answer.charCodeAt(i);
            }
            number=(number/3-100)/7-4;
            setSecretNumber(Math.pow(24,number)%55);
            if (secretNumber === 19) {
                props.changeShow("correctPhrase")
            }
            else{
                    props.changeShow("incorrectPhrase")
            }

        },2000)
    }

    const [answer,setAnswer] = useState("");

    useEffect(() => {

    },[answer]);

    if (props.show ==="riddle"){
        return (
            <div className='page'>
                <img src={x} alt='home' className='xSVG' onClick={() => props.changeShow("no show")}></img>
                <h1>Welcome to the current Riddle</h1>
                <p>1.) Solve the riddle and a secret phrase will appear. Be the first to say the phrase in the discord and win a small prize!</p> 
                <p>2.) Find a way to hack the website. Meaning, get the phrase by any way other than solving the riddle. Must show board members how this was acomplished and win $100!!! (this is designed to defend any hack)</p>
                <button onClick={()=> props.changeShow("riddleMessage")}>Continue</button>
            </div>
        )
    }
    else if (props.show === "riddleMessage"){
        return (
            <div className='page'>
                <img src={x} alt='home' className='xSVG' onClick={() => props.changeShow("no show")}></img>
                <h1>The Riddle</h1>
                <h3>Mountains without rocks</h3>
                <h3>Trees without leaves</h3>
                <h3>Buildings without bricks</h3>
                <h3>Water but no fish</h3>
                <h3>What am I?</h3>
                <form onSubmit={handleSubmit}>
                    <div>
                        <label name='answer'>Your answer</label>
                        <input type={Text} name='answer' id='answer' value={answer} onChange={handleChange}></input>
                    </div>
                </form>
                <button onClick={handleClick}>Submit</button>
            </div>
        )
    } else if (props.show === "incorrectPhrase") {
        return (
            <div className='page'>
                <img src={x} alt='home' className='xSVG' onClick={() => props.changeShow("no show")}></img>
                <h1>Incorrect phrase. Feel Free to try again!</h1>
            </div>
        )
    } else if (props.show === "correctPhrase") {
        return (
            <div className='page'>
                <img src={x} alt='home' className='xSVG' onClick={() => props.changeShow("no show")}></img>
                <h1>Correct!!!</h1>
                <h2>If you are the first to get it, type the answer to the discord and win a prize!</h2>
            </div>
        )
    }
 }
